def count_tokens(text: str) -> int:
    """Approximate token count (4 chars â‰ˆ 1 token)."""
    return len(text) // 4

def add(a: int, b: int) -> int:
    """Add two numbers."""
    return a + b

def divide(a: float, b: float) -> float:
    """Divide a by b."""
    if b == 0:
        raise ValueError("Cannot divide by zero")
    return a / b

def parse_github_url(url: str):
    import re
    url = url.rstrip('/')
    if url.endswith('.git'):
        url = url[:-4]
    pattern = r'(?:https?://)?(?:www\.)?github\.com/([^/]+)/([^/]+)'
    match = re.match(pattern, url)
    if not match:
        raise ValueError(f"Invalid GitHub URL: {url}")
    return match.groups()

def factorial(n: int) -> int:
    if n < 0:
        raise ValueError('n must be non-negative')
    if n == 0:
        return 1
    return n * factorial(n - 1)

def gcd(a: int, b: int) -> int:
    while b:
        a, b = b, a % b
    return abs(a)

def average(nums: list) -> float:
    if not nums:
        raise ValueError('List cannot be empty')
    return sum(nums) / len(nums)

def fibonacci(n: int) -> int:
    if n <= 0:
        return 0
    if n == 1:
        return 1
    return fibonacci(n-1) + fibonacci(n-2)

def min_max(nums: list) -> tuple:
    if not nums:
        raise ValueError('List cannot be empty')
    return (min(nums), max(nums))

def truncate(s: str, max_len: int) -> str:
    if len(s) <= max_len:
        return s
    return s[:max_len-3] + '...'

def linear_search(lst: list, target) -> int:
    for i, x in enumerate(lst):
        if x == target:
            return i
    return -1

def binary_search(lst: list, target) -> int:
    lo, hi = 0, len(lst) - 1
    while lo <= hi:
        mid = (lo + hi) // 2
        if lst[mid] == target:
            return mid
        if lst[mid] < target:
            lo = mid + 1
        else:
            hi = mid - 1
    return -1

def get_nested(d: dict, path: str, default=None):
    keys = path.split('.')
    for k in keys:
        d = d.get(k, {}) if isinstance(d, dict) else default
    return d if d != {} else default

def coalesce(*args):
    for a in args:
        if a is not None:
            return a
    return None

def normalize(value: float, min_val: float, max_val: float) -> float:
    if max_val == min_val:
        return 0.0
    return (value - min_val) / (max_val - min_val)

def lcm(a: int, b: int) -> int:
    def gcd(a, b):
        while b: a, b = b, a % b
        return abs(a)
    return abs(a * b) // gcd(a, b) if a and b else 0

def is_prime(n: int) -> bool:
    if n < 2:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return False
    return True
